import React, { Component } from "react";
import "semantic-ui-css/semantic.min.css";
import "./style/App.scss";
import LoginForm from "./components/Login/login";
import SignupForm from "./components/Signup/signup";
import Iot from "./components/Iot/Iot";
import Todo from "./components/Todo/Todo"
import Errorpage from "./components/Errorpage/Errorpage";
import { Route, Switch } from "react-router-dom";
import { MuiThemeProvider, createMuiTheme } from "@material-ui/core/styles";

const theme = createMuiTheme({
  palette: {
    primary: {
      light: "#fff",
      main: "#67bc46",
      dark: "#000"
    },
    secondary: {
      main: "#fff"
    }
  },
  typography: {
    useNextVariants: true
  }
});

class App extends Component {
  constructor() {
    super();
    this.state = {
      signupEmail: ""
    };
  }

  updateEmail = e => {
    this.setState({ signupEmail: e });
  };

  render() {
    return (
      <MuiThemeProvider theme={theme}>
        <Switch>
          <Route path="/" exact>
            <LoginForm updateEmail={this.updateEmail} />{" "}
          </Route>
          <Route path="/signup">
            <SignupForm email={this.state.signupEmail} />
          </Route>
          <Route path="/Iot">
            <Iot email={this.state.signupEmail} />
          </Route>
          <Route path="/*">
            <Errorpage email={this.state.signupEmail} />
          </Route>
          <Route path="/todo">
            <Todo/>
          </Route>
        </Switch>
      </MuiThemeProvider>
    );
  }
}
export default App;
