import React, { Component } from 'react'
import uuid from "uuid";
import  './Todo.scss'


class Todo extends Component {

    constructor(props) {
        super(props)

       this.input=React.createRef()
       this.state={
           list:[],
          }
    }

    addTask=()=>{

    const Items={
            id:uuid.v4(),
            value:this.input.current.value,
            Date: new Date().toUTCString()
        };

        if(localStorage.getItem('list')==null){
            const list=[]
            list.push(Items);
            localStorage.setItem("list",JSON.stringify(list))
        }
        else{
            const list=JSON.parse(localStorage.getItem('list'))
            list.push(Items)
            localStorage.setItem("list",JSON.stringify(list))
        }
        this.setState({
            list:JSON.parse(localStorage.getItem('list'))
        });
    }

    componentDidMount() {
        const list = window.localStorage.getItem('list');
        const parsedList = JSON.parse(list);
        if(list == null){
            return false
        }
        else{
            this.setState({
                list: parsedList,
            })
            console.log(this.state.list);
        }
    }

    deleteItem=(event)=> {

        let index = event.target.getAttribute('data-key')
        let listValue=JSON.parse(localStorage.getItem('list'));
        listValue.splice(index,1)
        this.setState({list:listValue});
        localStorage.setItem('list',JSON.stringify(listValue))
    }
    editItem=(event)=> {
        let index = event.target.getAttribute('data-key')
        let listValue=JSON.parse(localStorage.getItem('list'));
        listValue.splice(index,1)
        this.setState({list:listValue});
        localStorage.setItem('list',JSON.stringify(listValue))

        const Items={
            id:uuid.v4(),
            value:this.input1.current.value,
            Date: new Date().toUTCString()
        };
        if(localStorage.getItem('list')==null){
            const list=[]
            list.push(Items);
            localStorage.setItem("list",JSON.stringify(list))
        }
        else{
            const list=JSON.parse(localStorage.getItem('list'))
            list.push(Items)
            localStorage.setItem("list",JSON.stringify(list))
        }
        this.setState({
            list:JSON.parse(localStorage.getItem('list'))
        });
        // let index = event.target.getAttribute('data-key')
        // const Items={
        //     id:uuid.v4(),
        //     value:this.input.current.value,
        //     Date: new Date().toUTCString()
        // };
        // let listValue=JSON.parse(localStorage.setItem('list'));
        // listValue.splice(index,1)

        // const list=JSON.parse(localStorage.getItem('list'))
        //     list.push(Items)
        //     localStorage.setItem("list",JSON.stringify(list))
        // this.setState({
        //     list:JSON.parse(localStorage.getItem('list'))
        // });
    }
    render() {
        return (
            <div className="main-todo-container">
                <h1 className="Todo-header">Todo Tasks...</h1>
                <hr/>
                <div className="todo-container">
                    <input type="text" placeholder="Add Your Task..." ref={this.input}></input>
                        <button onClick={this.addTask} className="button" >Add</button>
                            <ol>
                                {
                                    this.state.list.map((item,index)=>
                                    {
                                        return(
                                            <li key={item.id}>

                                                <div className="task-container">
                                                    <div>
                                                        <input type="checkbox"></input>
                                                        <p className="task">{item.value}</p>
                                                    </div>
                                                    <div>
                                                        <input type="text" placeholder="Edit Task..." ref={this.input1} data-key={index} onChange={this.editItem}></input>
                                                        <button className="button" type="button" value="delete" data-key={index} onClick={this.deleteItem}>Delete</button>                                                    </div>
                                                </div>
                                            </li>)
                                        // return(
                                        // <li key={item.id}>
                                        //     <input type="checkbox"></input>
                                        //     <p className="task">{item.value}</p>
                                        //     <button className="button" type="button" value="delete" data-key={index} onClick={this.deleteItem}>Delete</button>
                                        //     <input type="text" onClick={this.editItem}></input>
                                        // </li>)
                                    })
                                } 
                            </ol>
                </div>

            </div>
        )
    }
}

export default Todo
